void IPL4asp__PT::incoming_message(const IPL4asp__Types::ASP__RecvFrom& incoming_par)
{
incoming_message(incoming_par, SYSTEM_COMPREF);
}

void IPL4asp__PT::incoming_message(const Socket__API__Definitions::PortEvent& incoming_par)
{
incoming_message(incoming_par, SYSTEM_COMPREF);
}

boolean IPL4asp__PT::process_message(const char *message_type, Text_Buf& incoming_buf, component sender_component, OCTETSTRING&)
{
if (!strcmp(message_type, "@IPL4asp_Types.ASP_RecvFrom")) {
IPL4asp__Types::ASP__RecvFrom incoming_par;
incoming_par.decode_text(incoming_buf);
incoming_message(incoming_par, sender_component);
return TRUE;
} else if (!strcmp(message_type, "@Socket_API_Definitions.PortEvent")) {
Socket__API__Definitions::PortEvent incoming_par;
incoming_par.decode_text(incoming_buf);
incoming_message(incoming_par, sender_component);
return TRUE;
} else return FALSE;
}


